                const user = {
                    email :"LoveWS@ho.com",
                    username:"WilliamShakespeareFan",
                    password: "trytry456",
                    passwordConf: "trytry456",
                    gender: "Female"
                };
                return request(server)
                    .post("/usersRegister")
                    .send(user)
                    .expect(200)
                    .then(res => {
                        expect(res.body.message).equals("User Successfully Added(Registered)!");
                        validID = res.body.data._id;
