                    .then(res => {
                        expect(res.body.message).equals("User Successfully Deleted!");
                    });
            });
            after(() => {
                return request(server)
                    .get(`/users/${validID}`)
                    .expect(200)
                    .expect([]);
            });
        });
        describe("when the id is invalid", () => {
            it("should return confirmation message and delete datastore", () => {
                return request(server)
                    .delete("/donations/1100001")
                    .expect(404)
                    .expect({});
            });
        });
    });
    describe("GET /usersLogout", () => {
        describe("when the id is valid", () => {
            it("should return the matching user", done => {
                request(server)
                    .get("/usersLogout")
                    .set("Accept", "application/json")
                    .expect("Content-Type", /json/)
                    .expect(200)
                    .end((err, res) => {
                        expect(res.body.message).equals("Successfully log out!");
                        done(err);
                    });
            });
        });
    });

});

